mapper.insert:

	select seq_board.nextval from dual


Insert into board(
	board_no, title, content, writer, reg_date
)values( 
	#{boardNo}, #{title}, #{content}, #{writer}, sysdate
)

mapper.update:
update board
set
	title = #{title},
	writer = #{writer},
	content = #{content}
where board_no = #{boardNo}
