mapper.insert:

	select seq_restboard.nextval from dual

insert into restboard(
	bo_no, bo_title, bo_content, bo_writer, bo_date, bo_hit
)values(
	#{boNo}, #{boTitle}, #{boContent}, #{boWriter}, sysdate, 0
)

insert into restboardfile(
	file_no, bo_no, file_name, file_size, file_fancysize, file_mime, file_savepath, file_downcount
)values(
	seq_restboardfile.nextval, #{boNo}, #{fileName}, #{fileSize}, #{fileFancysize}, #{fileMime}, #{fileSavepath}, 0
)

mapper.select:
	select 
		r.bo_no, bo_title, bo_content, bo_writer, bo_date, bo_hit, 
		file_no, file_name, file_size, file_fancysize, file_mime, file_savepath, file_downcount
	from restboard r left outer join restboardfile rf on(r.bo_no = rf.bo_no)
where r.bo_no = #{boNo}
